socket.bind("0.0.0.0", 8080)

loop do
  begin
    raw_packet, addr = socket.recvmsg_nonblock(500)
  rescue IO::WaitReadable
    retry
  end
  if raw_packet
    binding.irb
    socket.send("#{addr.inspect}\n", 0, addr)
  end
end
